pragma solidity 0.5.12;

contract coinFlip{

  // uint public balance;

  modifier costs(uint cost){
      require(msg.value >= cost);
      _;
  }

  event betResult(string result);
  event contractFunded(uint amount);

  function getBalance() public view returns(uint){
    return address(this).balance;
  }

  function fundContract() public payable{
    // balance += msg.value;
    emit contractFunded(getBalance());
  }

  function random() public view returns(uint){
    return now % 2;
  }

  function bet(uint answer) public payable costs(0.1 ether)returns(string memory){
    string memory result = "You re lost";
    string memory Rr = "Nope, you lost";

    if(answer == random()){
      msg.sender.transfer(msg.value * 2);
      // balance -= msg.value;
      result = "You re won";
      Rr = "Congratulation, you won";
    }else{
      // balance += msg.value;
    }

    emit betResult(result);
    return Rr;
  }

}
